    $my_query = new WP_Query($args);
    // 非常に違和感がある書き方をするが慣れんと仕方ない。
    // こういう書き方でデータを収集する。。。
    if ($my_query->have_posts()) : while ($my_query->have_posts()) : $my_query->the_post();
    ?>
      <li>
        <!-- 『the_permalink()』の内側に、
